import React, { useState, useEffect } from "react";
import { auth, googleAuthProvider } from "../../firebase";
import { toast } from "react-toastify";
import { Button } from "antd";
import { MailOutlined, GoogleOutlined } from "@ant-design/icons";
import { useDispatch, useSelector } from "react-redux";
import { Spin } from "antd";
import { Link } from "react-router-dom";
import { createOrUpdateUser } from "../../components/functions/auth";



const Login = ({ history }) => {
  const [email, setEmail] = useState("bharathchippa49@gmail.com");
  const [password, setPassword] = useState("Bharath11");
  let dispatch = useDispatch();
  const [loading, setLoading] = useState(false);


  const { user } = useSelector((state) => ({ ...state }));

  useEffect(() => {
    if (user && user.token) history.push("/");
  }, [user]);



  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const result = await auth.signInWithEmailAndPassword(email, password);
      //   console.log(result);
      const { user } = result;
      const idTokenResult = await user.getIdTokenResult();

      createOrUpdateUser(idTokenResult.token)
        .then((res) => {
          dispatch({
            type: "LOGGED_IN_USER",
            payload: {
              name: res.data.name,
              email: res.data.email,
              token: idTokenResult.token,
              role: res.data.role,
              _id: res.data._id,
            },
          });
        })
        .catch((err)=>console.log(err));

      history.push("/");
    } catch (error) {
      console.log(error);
      toast.error(error.message);
      setLoading(false);
    }
  };

  const googleLogin = async () => {
    auth
      .signInWithPopup(googleAuthProvider)
      .then(async (result) => {
        const { user } = result;
        const idTokenResult = await user.getIdTokenResult();

        createOrUpdateUser(idTokenResult.token)
          .then((res) => {
            dispatch({
              type: "LOGGED_IN_USER",
              payload: {
                name: res.data.name,
                email: res.data.email,
                token: idTokenResult.token,
                role: res.data.role,
                _id: res.data._id,
              },
            });
            roleBasedRedirect(res);
          })
          .catch((err)=>console.log(err));
          history.push("/");
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
        setLoading(false);
      });
  };

  const LoginForm = () => (
    <form onSubmit={handleSubmit}>
      <div>
        <input
          type="email"
          className="form-control"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          autoFocus
          placeholder="enter email"
        />
        <br />
        <input
          type="password"
          className="form-control"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          autoFocus
          placeholder="enter password"
        />
      </div>
      <br />
      <Button
        type="primary"
        className="mb-2"
        onClick={handleSubmit}
        block
        shape="round"
        icon={<MailOutlined />}
        size="medium"
        disabled={!email || password.length < 6}
      >
        Login with Email/Password
      </Button>
    </form>
  );

  return (
    <div className="container p-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <h4>Login</h4>
          {LoginForm()}
          <Button
            type="danger"
            className="mb-2"
            onClick={googleLogin}
            block
            shape="round"
            icon={<GoogleOutlined />}
            size="medium"
          >
            Login with Google
          </Button>
          <Link to="forgot/password" className="float-right text-danger">
            Forgot Password
          </Link>
          {loading ? (
            <Spin className="d-flex justify-content-sm-center" size="large" />
          ) : (
            <span />
          )}
        </div>
      </div>
    </div>
  );
};

export default Login;
